import React from 'react';
import logo from './logo.svg';
import './App.css';
import {FormList} from './FormList';
import { connect } from 'react-redux';
import {CALDERA_FORMS_STORE_SLUG} from './state';
import  {
	getForms,
} from './state/selectors';
import {
	setForms,
	setForm,
	newForm
} from './state/actions';

const App = (props) => {
	return (
		<div className="App">
			<header className="App-header">
				<img src={logo} className="App-logo" alt="logo" />
				<h1 className="App-title">Caldera State Example</h1>
			</header>
			<div>
				<FormList
					forms={props.forms}
					setForms={props.setForms}
					onFormUpdate={(newValue) => {
						props.setForm(newValue);
					}}
				/>
				<button
					onClick={props.addForm}
				>
                Add Form
				</button>
			</div>
		</div>
	);

};


const mapStateToProps = state => {
	state = state[CALDERA_FORMS_STORE_SLUG];
	let appState = {
		forms: getForms(state)

	};
	return appState;
};

const mapDispatchToProps = (dispatch) => {
	return {
		setForms: (forms) => {
			dispatch(setForms(forms));
		},
		setForm:(form) => {
			dispatch(setForm(form));
		},
		addForm(){
			dispatch(newForm());
		}
	};
};



export default connect(
	mapStateToProps,
	mapDispatchToProps
)(App);

import  React from 'react';
export const Form = (props) => {
	const form = props.form;
	return (
		<div
			id={`form-list-${form.ID}`}
		>
			<h2>
				{form.name}
			</h2>
			<input
				type={'text'}
				value={form.name}
				onChange={(event) => {
					return props.onFormUpdate(
						{
							...form,
							name:event.target.value
						}
					);
				}
				}
			/>
		</div>

	);
};

import React from 'react';
import {Form} from './Form';

export const FormList = (props) => {
	if (!Object.values(props.forms).length) {
		return (
			<div>
				<button
					onClick={() => {
						const forms = [
							{
								ID: 'CF1',
								name: 'Form One'
							},
							{
								ID: 'CF2',
								name: 'Form Two'
							},

							{
								ID: 'CF3',
								name: 'Form Three'
							}
						];
						props.setForms(forms);
					}}
				>
                    Add 3 Forms
				</button>
			</div>
		);
	}
	return (
		<div>
			<div>
				{Object.values(props.forms).map((form) => {
					return (
						<Form
							key={form.ID}
							form={form}
							onFormUpdate={props.onFormUpdate}
						/>

					);
				})}
			</div>
		</div>
	);
};

