const isLocalhost = Boolean(
	window.location.hostname === 'localhost' ||
    // [::1] is the IPv6 localhost address.
    window.location.hostname === '[::1]' ||
    // 127.0.0.1/8 is considered localhost for IPv4.
    window.location.hostname.match(
    	/^127(?:\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)){3}$/
    )
);

export default function register() {
	if (process.env.NODE_ENV === 'production' && 'serviceWorker' in navigator) {
		// The URL constructor is available in all browsers that support SW.
		const publicUrl = new URL(process.env.PUBLIC_URL, window.location);
		if (publicUrl.origin !== window.location.origin) {
			// Our service worker won't work if PUBLIC_URL is on a different origin
			// from what our page is served on. This might happen if a CDN is used to
			// serve assets; see https://github.com/facebookincubator/create-react-app/issues/2374
			return;
		}

		window.addEventListener('load', () => {
			const swUrl = `${process.env.PUBLIC_URL}/service-worker.js`;

			if (isLocalhost) {
				// This is running on localhost. Lets check if a service worker still exists or not.
				checkValidServiceWorker(swUrl);
			} else {
				// Is not local host. Just register service worker
				registerValidSW(swUrl);
			}
		});
	}
}

function registerValidSW(swUrl) {
	navigator.serviceWorker
		.register(swUrl)
		.then(registration => {
			registration.onupdatefound = () => {
				const installingWorker = registration.installing;
				installingWorker.onstatechange = () => {
					if (installingWorker.state === 'installed') {
						if (navigator.serviceWorker.controller) {
							// At this point, the old content will have been purged and
							// the fresh content will have been added to the cache.
							// It's the perfect time to display a "New content is
							// available; please refresh." message in your web app.
							console.log('New content is available; please refresh.');
						} else {
							// At this point, everything has been precached.
							// It's the perfect time to display a
							// "Content is cached for offline use." message.
							console.log('Content is cached for offline use.');
						}
					}
				};
			};
		})
		.catch(error => {
			console.error('Error during service worker registration:', error);
		});
}

function checkValidServiceWorker(swUrl) {
	// Check if the service worker can be found. If it can't reload the page.
	fetch(swUrl)
		.then(response => {
			// Ensure service worker exists, and that we really are getting a JS file.
			if (
				response.status === 404 ||
        response.headers.get('content-type').indexOf('javascript') === -1
			) {
				// No service worker found. Probably a different app. Reload the page.
				navigator.serviceWorker.ready.then(registration => {
					registration.unregister().then(() => {
						window.location.reload();
					});
				});
			} else {
				// Service worker found. Proceed as normal.
				registerValidSW(swUrl);
			}
		})
		.catch(() => {
			console.log(
				'No internet connection found. App is running in offline mode.'
			);
		});
}

export function unregister() {
	if ('serviceWorker' in navigator) {
		navigator.serviceWorker.ready.then(registration => {
			registration.unregister();
		});
	}
}

export const SET_FORMS = 'SET_FORMS';
export const SET_FORM = 'SET_FORM';
export const ADD_FORM_PREVIEW = 'ADD_FORM_PREVIEW';
export const NEW_FORM = 'NEW_FORM';

/**
 * Action creator for setting a form in state
 *
 * @param form
 * @returns {{type: string, form: *}}
 */
export const setForm = (form) => {
	return {
		type: SET_FORM,
		form: form
	};
};

/**
 * Action creator for setting all forms in state
 *
 * @param forms
 * @returns {{type: string, forms: *}}
 */
export const setForms = ( forms ) => {
	return {
		type: SET_FORMS,
		forms:forms
	};
};

/**
 * Action creator for setting one form in state
 *
 * @param formId
 * @param preview
 * @returns {{type: string, formId: *, preview: *}}
 */
export const  addFormPreview = (formId,preview)=> {
	return {
		type: ADD_FORM_PREVIEW,
		formId: formId,
		preview:preview
	};
};


/**
 * Action creator to add an empty form to state
 *
 * @returns {{type: string}}
 */
export const newForm = () => {
	return {
		type: NEW_FORM
	};
};

export const SET_EDIT_FORM = 'SET_EDIT_FORM';
export const UNSET_EDIT_FORM = 'UNSET_EDIT_FORM';
export const SET_FORM_PRIVACY_SETTINGS = 'SET_FORM_PRIVACY_SETTINGS';

/**
 * Set the current form to edit in privacy settings
 *
 * @param {String} formId
 * @returns {{type: string, formId: *}}
 */
export const setEditForm = (formId ) => {
	return {
		type: SET_EDIT_FORM,
		formId: formId
	};
};

/**
 * Unset the current form to edit in privacy settings
 *
 * @returns {{type: string}}
 */
export const unsetEditForm = () => {
	return {
		type: UNSET_EDIT_FORM,
	};
};

/**
 * Set privacy form settings
 *
 * @param settings
 * @returns {{type: string, form: *}}
 */
export const setFormPrivacyForm = (settings) => {
	return {
		type: SET_FORM_PRIVACY_SETTINGS,
		form: settings
	};
};

import {
	setForms,
	setForm,
	addFormPreview
} from './actions';

import {
	getForm,
	getForms,
	getFormPreview,
	getFormPreviews
} from './selectors';

import {
	formsReducer,
} from './reducers';


import {
	combineReducers,createStore
} from 'redux';

const actionFunctions = {
	setForms,
	setForm,
	addFormPreview
};

const selectorFunctions = {
	getForm,
	getForms,
	getFormPreview,
	getFormPreviews
};


export const CALDERA_FORMS_STORE_SLUG = 'CALDERA_FORMS/FORMS';
/**
 * Caldera Forms Redux-store
 *
 * @type {{reducer: formsReducer, actions: {setForms: setForms, setForm: setForm, addFormPreview: addFormPreview}, selectors: {getForm: getForm, getForms: getForms, getFormPreview: getFormPreview, getFormPreviews: getFormPreviews}}}
 */
export const calderaFormsFormState = {
	reducer: formsReducer,
	actions: actionFunctions,
	selectors: selectorFunctions,
};

const reducers = {};
reducers[CALDERA_FORMS_STORE_SLUG] = calderaFormsFormState.reducer;

export const calderaFormsReduxStore = createStore(
	combineReducers(reducers),
	window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);

