			privacySettingsReducer
		}
	},
	state: {
		CALDERA_FORMS_STORE_SLUG,
		calderaFormsFormState,
		calderaFormsReduxStore
	},
	util: {
		formHasId,
		findFormById,
		setFormInState,
		generateId,
		mapArrayOfFormsToObject,
		getFormId,
		findFormIndexById
	}
