import {
	SET_FORMS,
	SET_FORM,
	ADD_FORM_PREVIEW,
	NEW_FORM
} from './actions';

import {SET_FORM_PRIVACY_SETTINGS} from './actions.privacy';

import {
	setFormInState
} from './util';


/**
 * Default state for formsReducer
 *
 * @type {{forms: Array, formPreviews: {}}}
 */
export const DEFAULT_STATE = {
	forms: [],
	formPreviews: {},
};

/**
 * Create inertial state from an array of forms.
 *
 * @param {Object} forms
 * @returns {{} & {forms: *} & {forms: Array, formPreviews: {}}}
 */
export const initialStateWithForms = (forms) => {
	if( ! Array.isArray(forms) ){
		throw 'You must use an array of forms!';
	}
	return Object.assign({}, {forms:forms},DEFAULT_STATE );
};


/**
 * Reducer for form(s) state
 *
 * @param {Object}state
 * @param {Object} action
 * @returns {*}
 */
export const formsReducer = (state = DEFAULT_STATE, action) => {
	switch (action.type) {
	case SET_FORMS:
		return {
			...state,
			forms:action.forms
		};
	case ADD_FORM_PREVIEW:
		state.formPreviews[action.formId] = action.preview;
		return {
			...state,
			formPreviews: state.formPreviews
		};
	case SET_FORM :
		return( setFormInState(state,action.form));
	case NEW_FORM:
		const newForm = {
			ID: '11',
			name: '...'
		};
		state.forms.push(newForm);
		return Object.assign({},state);

	default:
		return state;
	}

};

/**
 * Reducer for form privacy settings state
 *
 * @param {Object}state
 * @param {Object} action
 * @returns {*}
 */
export const privacySettingsReducer = ( state = {
	...DEFAULT_STATE
}, action ) => {
	switch (action.type){
	case SET_FORM_PRIVACY_SETTINGS :
		return setFormInState(state, action.form);
	default:
		return state;
	}
};

import {
	findFormById,
	mapArrayOfFormsToObject
} from './util';

/**
 * Selector to find forms in state
 *
 * @param {Object}state
 * @param {String}formId
 * @returns {boolean|*}
 */
export const getForm = ( state, formId )  => {
	return findFormById(state.forms, formId);
};

/**
 * Selector to get all forms in state
 *
 * @param {Object} state
 * @returns {*}
 */
export const getForms = ( state ) => {
	let forms = {};
	if( Array.isArray(state.forms)&&state.forms.length){
		forms = mapArrayOfFormsToObject(state.forms);
	}else{
		forms = state.forms;
	}
	return forms;
};


/**
 * Get a form preview from state
 *
 * @param {Object}state
 * @param {String}formId
 * @returns {string}
 */
export const getFormPreview = ( state,formId ) => {
	return state.formPreviews.hasOwnProperty( formId )
		?state.formPreviews[ formId ]
		: '';

};

/**
 * Get all form previews in state
 *
 * @param {Object}state
 * @returns {{}|DEFAULT_STATE.formPreviews|formPreviews|{CF2}}
 */
export const getFormPreviews = (state) => {
	return state.formPreviews;
};

import {findFormById} from './util';

/**
 * Get all of a form's privacy settings.

 * @param {String} formId
 * @param {Object} state
 * @returns {*}
 */
export const getFormPrivacySettings = (formId,state) =>{
	const settings = findFormById(state.forms, formId);
	return 'object' === typeof settings ? settings : false;
};

/**
 * Check if a form has the provided ID
 *
 * @param {Object} form Form config
 * @param {String} formId
 * @return {boolean}
 */
export const formHasId = ( form, formId ) => {
	if( 'object' !== typeof  form ){
		return false;
	}
	if( form.hasOwnProperty('ID') ){
		return formId === form.ID;
	}
	if( form.hasOwnProperty('formId') ){
		return formId === form.formId;
	}
	return false;
};

/**
 * Get form ID
 *
 * @param {Object} form
 * @returns {String|bool}
 */
export const getFormId = (form) => {
	if( form.hasOwnProperty('ID') ){
		return form.ID;
	}
	if( form.hasOwnProperty('formId') ){
		return form.formId;
	}
	return false;
};

/**
 * Makes sure forms are an array keyed by ID
 *
 * @param {Object|Array}forms
 * @returns {Object}
 */
export const mapArrayOfFormsToObject = (forms ) => {

	if (Array.isArray(forms)) {
		let preparedForms = {};
		forms.map((form) => {
			if (false !== getFormId(form)) {
				preparedForms[getFormId(form)] = form;
			}
			return true;
		});

		return preparedForms;
	}else if( 'object' === typeof forms ){
		return forms;
	}else{
		return {};
	}

};

/**
 * Find form by form ID ID in an array of forms
 *
 * @param {Array} forms
 * @param {String} formId
 */
export const findFormById = (forms, formId,) => {
	if(!Array.isArray(forms) || ! forms.length ){
		return false;
	}
	return forms.find(form => {
		return formHasId(form,formId);
	});
};

/**
 * Find array index  in an array of forms by form ID
 *
 * @param {Array} forms
 * @param {String} formId
 */
export const findFormIndexById = (forms, formId) => {
	if(!Array.isArray(forms) || ! forms.length ){
		return false;
	}
	return forms.findIndex(form => {
		return formHasId(form,formId);
	});
};

/**
 * Generate ID for things
 *
 * @param {String} generateFor
 * @returns {string}
 */
export const generateId = (generateFor) =>{
	const id =  Math.round( Math.random() * 10000000 );
	let prefix = 'cf_';
	switch (generateFor) {
	case 'row':
		prefix = 'row_';
		break;
	case 'column':
		prefix = 'col_';
		break;
	default:
		prefix = 'cf_';
		break;

	}

	return `${prefix}${id}`;
};

export const setFormInState = (state,form) => {
	if( ! Array.isArray( state.forms) || ! state.forms.length) {
		return {
			...state,
			forms: [form]
		};
	}else{
		const index = findFormIndexById(state.forms, form.ID);
		if (-1 <= index) {
			state.forms.splice(index, 1, form);
		} else {
			state.forms.push(form);
		}

	}
	return {
		...state,
		forms:state.forms
	};
};
